public class comparison {
    public static void main(String[] args) {
        int x = 80;
        int y = 50;
        System.out.println(x<y);
        System.out.println(x>y);
        System.out.println(x<=y);
        System.out.println(x>=y);
        System.out.println(x==y);
        System.out.println(x!=y);


        int passwordlength = 6;
        System.out.println(passwordlength>5);
        System.out.println(passwordlength<5);
        System.out.println(passwordlength==6);
        System.out.println(passwordlength!=2);
    }
}
